    public function fetch_markets ($params = array ()) {
    	$response = $this->fetch ('https://web.crossexchange.io/bb/symbol/all?leverageType=0');
    	$pairs = $response['data']['bbPairList'];
        $ret = array ();
        for ($p = 0; $p < count ($pairs); $p++) {
        	$value = $pairs[$p];
        	$symbol = $value['name'];
            $parts = explode ('_', $symbol);
            $base = $parts[1];
            $quote = $parts[0];

            $ret[] = array (
                'id' => $symbol,
                'symbol' => $symbol,
                'base' => $base,
                'quote' => $quote,
            );
        }

        return $ret;
    }

    public function fetch_balance ($params = array ()) {
        $this->load_markets();
        $response = $this->privateGetGETV1ApiAuthWallet ($params);
        $result = array ( 'info' => $response );
        $free = $response['data']['free'];
        $freezed = $response['data']['freezed'];
        foreach($free as $key=>$value)
        {
            $coin_type = $key;
            $account = $this->account ();
            $account['free'] = $value;
            $account['used'] = $freezed[$coin_type];
            $account['total'] = $this->sum ($account['free'], $account['used']);
            $result[$coin_type] = $account;
        }
        return $this->parse_balance($result);
    }

