    public function parse_trade ($trade, $market) {
        $side = 'sell';
        $price = (float) $trade[1];
        $epsilon = 0.00000000001;
        if($price > $epsilon)
            $side = 'buy';
        $timestamp = $trade[3];
        $fee_num = null;
        if( count($trade) >=7 )
            $fee_num = (float) $trade[6];
        return array (
            'id' => $trade[0],
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601 ($timestamp),
            'symbol' => $trade[5],
            'order' => null,
            'type' => $trade[4],
            'side' => $side,
            'price' => $price,
            'amount' => (float) $trade[2],
            'fee' => $fee_num,
        );
    }

    public function fetch_trades ($symbol, $since = null, $limit = null, $params = array ()) {
        $this->load_markets();
        $market = $this->market ($symbol);
        $response = $this->publicGetGETV1ApiTrades (array_merge (array (
            'pair' => $market['id'],
        ), $params));
        $trades = $response['data'];
        $new_trade = array();
        $num = count($trades); 
         for($i=0;$i<$num;++$i)
         {
            $one_trade = array();
            $count = count($trades[$i]);
            for($k = 0; $k < $count; ++$k)
                  array_push($one_trade, $trades[$i][$k]);

            array_push($one_trade, $symbol);
            array_push($new_trade, $one_trade);
         } 
        return $this->parse_trades($new_trade, $market, $since, $limit);
    }

